import React, { useState } from 'react';
import axios from 'axios';
import './Form.css';

function Update() {
    const [data, setData] = useState({ id: '', name: '', description: '', price: '', quantity: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.put(`/api/items/${data.id}`, data)
            .then((response) => {
                console.log(response.data);
                setData({ id: '', name: '', description: '', price: '', quantity: '' });
            })
            .catch((error) => {
                console.error(error);
            });
    };

    return (
        <form className="form" onSubmit={handleSubmit}>
            <h2>Update Item</h2>
            <input type="text" name="id" value={data.id} onChange={handleChange} placeholder="ID" required />
            <input type="text" name="name" value={data.name} onChange={handleChange} placeholder="Name" required />
            <input type="text" name="description" value={data.description} onChange={handleChange} placeholder="Description" required />
            <input type="number" name="price" value={data.price} onChange={handleChange} placeholder="Price" required />
            <input type="number" name="quantity" value={data.quantity} onChange={handleChange} placeholder="Quantity" required />
            <button type="submit">Update</button>
        </form>
    );
}

export default Update;
