import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:8080/api',
    headers: {
        'Content-Type': 'application/json',
    },
});
// Função para buscar todos os itens
export const fetchItems = async () => {
    try {
        const response = await api.get('/items');
        return response.data || [];  // Garante que retorna um array, mesmo se a API retornar `null` ou `undefined`
    } catch (error) {
        console.error('Error fetching items:', error);
        throw error;  // Repassa o erro para ser tratado pelo chamador
    }
};

// Função para criar um novo item
export const createItem = async (item) => {
    try {
        const response = await api.post('/items', item);
        return response.data;
    } catch (error) {
        console.error('Error creating item:', error);
        throw error;
    }
};

// Função para atualizar um item existente
export const updateItem = async (id, item) => {
    try {
        console.log('Updating item with ID:', id, 'Data:', item);  // Log para depuração
        const response = await api.put(`/items/${id}`, item);
        return response.data;  // Retorna os dados da resposta
    } catch (error) {
        console.error('Error updating item:', error);
        throw error;  // Repassa o erro para ser tratado pelo chamador
    }
};

// Função para deletar um item
export const deleteItem = async (id) => {
    try {
        console.log('Deleting item with ID:', id);  // Log para depuração
        const response = await api.delete(`/items/${id}`);
        return response.data;  // Retorna os dados da resposta
    } catch (error) {
        console.error('Error deleting item:', error);
        throw error;  // Repassa o erro para ser tratado pelo chamador
    }
};

export default api;
