import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './List.css';

function Read() {
    const [items, setItems] = useState([]);

    useEffect(() => {
        axios.get('/api/items')
            .then((response) => {
                setItems(response.data);
            })
            .catch((error) => {
                console.error(error);
            });
    }, []);

    return (
        <div className="list">
            <h2>Items List</h2>
            <ul>
                {items.map((item) => (
                    <li key={item.id}>
                        <strong>{item.name}</strong> - {item.description} - ${item.price} - Qty: {item.quantity}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Read;
