import React, { useState, useEffect } from 'react';
import { fetchItems, createItem } from '../services/api';

function ItemsList() {
    const [items, setItems] = useState([]);  // Inicializa items como um array vazio

    useEffect(() => {
        const loadItems = async () => {
            try {
                const data = await fetchItems();
                setItems(data);  // Atualiza o estado com os itens recebidos
            } catch (error) {
                console.error('Failed to load items:', error);
            }
        };

        loadItems();
    }, []);  // useEffect rodará apenas uma vez após o componente montar

    const handleCreate = async (item) => {
        try {
            const newItem = await createItem(item);
            setItems([...items, newItem]);  // Adiciona o novo item ao array existente
        } catch (error) {
            console.error('Failed to create item:', error);
        }
    };

    return (
        <div>
            <h1>Items List</h1>
            <ul>
                {items.map((item, index) => (
                    <li key={index}>{item.name}</li>  // Renderiza o nome de cada item
                ))}
            </ul>
            <button onClick={() => handleCreate({ name: 'Novo Item', description: 'Descrição', price: 10, quantity: 1 })}>
                Add Item
            </button>
        </div>
    );
}

export default ItemsList;
