import React, { useState, useEffect } from 'react';
import { fetchItems, createItem } from '../services/api';
import '../styles/ItemsList.css';  // Certifique-se de que o CSS está corretamente importado

function ItemsList() {
    const [items, setItems] = useState([]);  // Inicializa items como um array vazio
    const [error, setError] = useState(null);  // Estado para armazenar erros

    // Carrega os itens do backend quando o componente é montado
    useEffect(() => {
        const loadItems = async () => {
            try {
                const data = await fetchItems();
                setItems(data);  // Atualiza o estado com os itens recebidos
            } catch (error) {
                console.error('Failed to load items:', error);
                setError('Failed to load items');
            }
        };

        loadItems();
    }, []);  // useEffect só roda uma vez ao montar

    // Função para criar um novo item
    const handleCreate = async () => {
        const newItem = {
            name: 'Novo Item',
            description: 'Descrição do novo item',
            price: 10,
            quantity: 1
        };

        try {
            const createdItem = await createItem(newItem);
            setItems([...items, createdItem]);  // Adiciona o novo item ao array existente
        } catch (error) {
            console.error('Failed to create item:', error);
            setError('Failed to create item');
        }
    };

    return (
        <div className="items-list">
            <h1>Items List</h1>
            {error && <p className="error">{error}</p>}  // Mostra uma mensagem de erro se houver

            <ul>
                {items.length > 0 ? (
                    items.map((item, index) => (
                        <li key={index}>
                            <strong>{item.name}</strong> - {item.description} - ${item.price} - Qty: {item.quantity}
                        </li>
                    ))
                ) : (
                    <li>No items available</li>  // Mostra uma mensagem se a lista estiver vazia
                )}
            </ul>

            <button onClick={handleCreate} className="btn">Add Item</button>  {/* Botão para adicionar um novo item */}
        </div>
    );
}

export default ItemsList;
